import React, { useEffect } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import { connect } from 'react-redux';
import { currentLinkAction } from '../actions';
import { AppBar, Toolbar, Typography, Drawer, List, ListItem, ListItemIcon, ListItemText, Container } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import SwapHorizIcon from '@mui/icons-material/SwapHoriz';
import UserDashboard from './UserDashboard';
import UserTransfer from './UserTransfer';

const drawerWidth = 240;

const UserHeader = (props) => {
  const location = useLocation();

  useEffect(() => {
    const route = location.pathname.split('/').pop();
    if (route === 'user-dashboard') {
      props.currentLinkAction('User-dashboard');
    } else if (route === 'user-transfer') {
      props.currentLinkAction('User-transfer');
    }
  }, [location.pathname, props]);

  const getTitle = () => {
    const titles = {
      'User-dashboard': 'Dashboard',
      'User-transfer': 'Transfer Tokens',
    };
    return titles[props.currentLinkState] || 'Dashboard';
  };

  return (
    <div style={{ display: 'flex' }}>
      <AppBar position="fixed" style={{ zIndex: 1300 }}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            {getTitle()}
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Toolbar />
        <List>
          <ListItem button component={Link} to="/user-dashboard" onClick={() => props.currentLinkAction('User-dashboard')}>
            <ListItemIcon>
              <HomeIcon />
            </ListItemIcon>
            <ListItemText primary="Dashboard" />
          </ListItem>
          <ListItem button component={Link} to="/user-transfer" onClick={() => props.currentLinkAction('User-transfer')}>
            <ListItemIcon>
              <SwapHorizIcon />
            </ListItemIcon>
            <ListItemText primary="Transfer Tokens" />
          </ListItem>
        </List>
      </Drawer>
      <main style={{ flexGrow: 1, padding: '24px' }}>
        <Toolbar />
        <Container>
          <Routes>
            <Route path="/user-dashboard" element={<UserDashboard />} />
            <Route path="/user-transfer" element={<UserTransfer />} />
          </Routes>
        </Container>
      </main>
    </div>
  );
};

const mapStateToProps = (state) => {
  return { currentLinkState: state.currentLinkState };
};

export default connect(mapStateToProps, { currentLinkAction })(UserHeader);
