import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './styling/AdminDashboard.css';
import './MailEntry'
import MailEntry from './MailEntry';
import ReportEntry from './ReportEntry';
import ReportPopUp from './ReportPopUp';
import MailPopUp from './MailPopUp';

const AdminDashboard = () => {
  const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const USERS_URL = '/api/user';

  const [user, setUser] = useState({});

  useEffect(() => {
    const controller = new AbortController();

    const fetchAdmin = async () => {
        try {
            const response = await axios.get(`${Admin_URL}/me`, {
                signal: controller.signal,
                headers: headers,
            });
            setAdmin(response.data.Admin);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch user request cancelled`);
            }
            else {
                console.error(`Error getting user:` , error);
            }
        }
    }; 

    const fetchMail = async () => {
        try {
            const response = await axios.get(`${MAIL_URL}/incoming`, {
                signal: controller.signal,
                headers: headers,
            });
            setMail(response.data.mail);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch mail request cancelled`);
            }
            else {
                console.error(`Error getting mail:` , error);
            }
        }
    }; 

    const fetchAppCount = async () => {
      try {
        const response = await axios.get(`${APP_URL}/count/Ongoing`, {
            signal: controller.signal,
            headers: headers,
        });
        setAppCount(response.data.appCount);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch application count request cancelled`);
          }
          else {
              console.error(`Error getting application count:` , error);
          }
      }
    }

    const fetchReports = async () => {
        try {
            const response = await axios.get(`${REPORT_URL}/plus`, {
                signal: controller.signal,
                headers: headers,
            });
            setReports(response.data.reports);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch reports request cancelled`);
            }
            else {
                console.error(`Error getting reports:` , error);
            }
        }
    };

    const fetchNote = async () => {
        try {
            const response = await axios.get(`${NOTE_URL}`, {
                signal: controller.signal,
                headers: headers,
            });
            setNote(response.data.note || '');
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch note request cancelled`);
            }
            else {
                console.error(`Error getting note:` , error);
            }
        }
    }

    const fetchLab = async () => {
      try {
        const response = await axios.get(`${LAB_URL}/me`, {
            headers: headers,
        });
        setLab(response.data.lab);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch lab request cancelled`);
          }
          else {
              console.error(`Error getting lab:` , error);
          }
      }
    };

    fetchAdmin();
    fetchLab();
    fetchMail();
    fetchAppCount();
    fetchReports();
    fetchNote();

    return () => {
        controller.abort();
    };
  }, []);


  const handleChange = (e) => {
      setNote(e.target.value);
  };

  const handleSave = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.put(NOTE_URL, {note}, {
              headers: headers,
          });
          //console.log('Note saved:', response.data);
          alert('Note saved');
      } catch (error) {
          //console.error('Error saving:', error);
          alert('Failed to save note: ' + error.message);
      }
  };

  const viewMail = (mail) => {
    setCurMail(mail);
    setMailPopUpVisible(true);
  }

  const closeMail = () => {
    setMailPopUpVisible(false);

    const readMail = async () => {
      try {
        const response = await axios.put(`${MAIL_URL}/${curMail._id}`, {isRead: true}, {
          headers: headers,
        });
        console.log(response.data);
      } catch (error) {
        console.log('Failed to update read status of mail: ' + error);
      }
    };

    if(!curMail.isRead) {
      readMail();
      setCurMail(prevState => ({
        ...prevState, isRead: true,
      }));
    }
  }

  const viewReport = (report) => {
    //console.log(report.file ? true : false);
    setCurReport(report);
    setReportPopUpVisible(true);
  };

  const closeReport = () => {
    setReportPopUpVisible(false);

    const readReport = async () => {
      try {
        const response = await axios.put(`${REPORT_URL}/${curReport._id}`, {isRead: true}, {
          headers: headers,
        });
        console.log(response.data);
      } catch (error) {
        console.log('Failed to update read status of report: ' + error);
      }
    };

    if(!curReport.isRead) {
      readReport();
      setCurReport(prevState => ({
        ...prevState, isRead: true,
      }));
    }
  };

return (
  <div className = "AdminDashboard-wrapper">
    {
      Admin.labId ? (
<body>
  <div className="AdminDashboard-header">
      <h1>{lab.name}</h1>
      <h2>{Admin.name}</h2>
  </div>
  <div className="AdminDashboard-panel">
      <div className="AdminDashboard-upper-half">
          <div className="AdminDashboard-upper-half-left">
              <h3>Unread Mail</h3>
              <div className="AdminDashboard-button-list">
                  {mail.length > 0 ? (
                      mail
                      .filter(currentMail => !currentMail.isRead)
                      .map((currentMail, index) => (
                      <MailEntry 
                          key = {index}
                          mail = {currentMail} 
                          mailType = 'incoming'
                          onClickFunction = {() => viewMail(currentMail)}/>
                          ))
                      ) : (
                          <p>No incoming mail.</p>
                  )}
              </div>
          </div>
          <div className = "AdminDashboard-upper-half-right">
            <div className = "AdminDashboard-ongoing-apps">
              <h3>Ongoing Applications</h3>
              <h1>{appCount}</h1>
            </div>
            <div className = "AdminDashboard-lab-slots"> 
              <h3>Lab Slots</h3>
              <h1>{lab.slots}/{lab.maxSlots}</h1>
            </div>
          </div>
      </div>
      <div className="AdminDashboard-lower-half">
          <div className="AdminDashboard-box half">
              <h3>Submitted Progress Reports</h3>
              <div className="AdminDashboard-button-list">
                  {reports.length > 0 ? (
                      reports
                      .filter(report => !report.isRead)
                      .map((report, index) => (
                      <ReportEntry
                          className = "reportEntries"
                          key = {index}
                          report = {report} 
                          onClickFunction = {() => viewReport(report)}/>
                          ))
                      ) : (
                          <p>No reports.</p>
                  )}
              </div>
          </div>
          <div className="AdminDashboard-box half">
              <h3>Personal Notes</h3>
              <form id="AdminDashboard-noteForm">
                  <textarea 
                      id="noteText"
                      name='text'
                      value = {note.text} 
                      rows="4" 
                      cols="50" 
                      placeholder="Enter your notes here..."
                      onChange = {handleChange}>
                  </textarea>
                  <button 
                      onClick = {handleSave}>Save Note</button>
              </form>
          </div>
      </div>
  </div>
  <ReportPopUp 
      id = 'reportPopUp'
      studentName = ''
      report = {curReport}
      isVisible = {isReportPopUpVisible} 
      closeFunction = {() => closeReport()}/>
  <MailPopUp
      id = 'mailPopUp'
      studentName = ''
      mail = {curMail}
      mailType = 'incoming'
      isVisible = {isMailPopUpVisible}
      closeFunction = {() => closeMail()}/>
</body>
      ) : (
      <h1>
        You have not created a lab yet.   
      </h1>
      )
    }

    </div>
  )
}

export default AdminDashboard;
