import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styling/ProfessorReportSubmission.css';
import ReportPopUp from './ReportPopUp';

const ProfessorReportSubmission = () => {
const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }
  const REPORT_URL = '/api/reports';

  const [reports, setReports] = useState([]);
  const [curReport, setCurReport] = useState({});
  const [isReportPopUpVisible, setReportPopUpVisible] = useState(false);

  useEffect(() => {
    const controller = new AbortController();

    const fetchReports = async () => {
      try {
          const response = await axios.get(`${REPORT_URL}/plus`, {
              signal: controller.signal,
              headers: headers,
          });
          console.log(response.data.reports);
          setReports(response.data.reports);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch reports request cancelled`);
          }
          else {
              console.error(`Error getting reports:` , error);
          }
      }
    };

    fetchReports();

    return () => {
      controller.abort();
    };
  }, []);

  const viewReport = (report) => {
    setCurReport(report);
    setReportPopUpVisible(true);
  };

  const closeReport = () => {
    setReportPopUpVisible(false);

    const readReport = async () => {
      try {
        const response = await axios.put(`${REPORT_URL}/${curReport._id}`, {isRead: true}, {
          headers: headers,
        });
        console.log(response.data);
      } catch (error) {
        console.log('Failed to update read status of report: ' + error);
      }
    };

    if(!curReport.isRead) {
      readReport();
      setCurReport(prevState => ({
        ...prevState, isRead: true,
      }));
    }
  };

  return (
    <>
      <div className="reportSubmission-wrapper">
        <div className="reportSubmission-panel">
          <table>
            <thead>
              <tr>
                <th>Read</th>
                <th>Students' Name</th>
                <th>Student ID</th>
                <th>Title</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {reports.map(report => (
                <tr key={report._id}>
                  <td><input type="checkbox" checked={report.isRead} disabled /></td>
                  <td>{report.studentInfo.name}</td>
                  <td>{report.student}</td>
                  <td>{report.title}</td>
                  <td><button onClick = {() => viewReport(report)}>View Report</button></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <ReportPopUp 
      id = 'reportPopUp'
      studentName={curReport.studentInfo?.name || ''}
      report = {curReport}
      isVisible = {isReportPopUpVisible} 
      closeFunction = {() => closeReport()}/>
    </>
  );
}

export default ProfessorReportSubmission;
