import React, { useState, useEffect} from 'react';
import axios from 'axios';
import './styling/ProfessorLabInfo.css';

const majors = {
  "Mathematics": ["Graph Theory", "Algebra", "Calculus"],
  "Computer Science": ["Artificial Intelligence", "Machine Learning", "Software Engineering"],
  "Physics": ["Quantum Mechanics", "Thermodynamics", "Astrophysics"],
  "Biology": ["Genetics", "Microbiology", "Ecology"],
  "Chemistry": ["Organic Chemistry", "Inorganic Chemistry", "Physical Chemistry"]
};

const languages = ['Korean', 'English'];

const validateInput = (labData) => {
  const { name, labPosition, major, submajor, description, expectations, language, salary, maxSlots, website } = labData;

  if (!name || !labPosition || !major || !submajor || !description || !expectations || !language || !salary || !maxSlots || !website) {
    return 'All fields except masters are required';
  }

  if (!Object.keys(majors).includes(major)) {
    return 'Invalid major selected';
  }

  if (!majors[major].includes(submajor)) {
    return 'Invalid submajor selected';
  }

  if (!languages.includes(language)) {
    return 'Invalid language selected';
  }

  const websitePattern = /^(https?|chrome):\/\/[^\s$.?#].[^\s]*$/gm;
  if (!websitePattern.test(website)) {
    return 'Invalid website URL format';
  }

  if (isNaN(salary) || salary < 0 || salary > 1000000) {
    return 'Salary should be a number between 10,000 and 1,000,000';
  }

  if (isNaN(maxSlots) || maxSlots < 3 || maxSlots > 10) {
    return 'Max slots should be a number between 3 and 10';
  }

  return null;
};

const ProfessorLabInfo = () => {
  const token = localStorage.getItem('token');
  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const USER_URL = '/api/users';
  const LAB_URL = '/api/labs';

  const [user, setUser] = useState({});
  const [labData, setLabData] = useState({
    name: '',
    labPosition: '',
    major: '',
    submajor: '',
    description: '',
    expectations: '',
    language: '',
    salary: '',
    maxSlots: '',
    masters: false,
    website: '',
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
          const response = await axios.get(`${USER_URL}/me`, {
              headers: headers,
          });
          setUser(response.data.user);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch user request cancelled`);
          }
          else {
              console.error(`Error getting user:` , error);
          }
      }
    };

    const fetchLab = async () => {
      try {
        const response = await axios.get(`${LAB_URL}/me`, {
            headers: headers,
        });
        console.log(response.data.lab);
        setLabData(response.data.lab);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch lab request cancelled`);
          }
          else {
              console.error(`Error getting lab:` , error);
          }
      }
    };
    fetchUser();
    fetchLab();

  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setLabData({
      ...labData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const validationError = validateInput(labData);
    if (validationError) {
      alert(validationError);
      return;
    }

    if (user.labId) {
      try {
        const response = await axios.put(`${LAB_URL}`, labData, {
          headers: headers,
        });
        alert('Lab edited successfully!');
      } catch (error) {
        console.error('Error editing lab:', error);
        alert('Failed to edit lab');
      }
    }
    else {
      try {
        const response = await axios.post(`${LAB_URL}`, labData, {
          headers: headers,
        });
        alert('Lab created successfully!');
      } catch (error) {
        console.error('Error creating lab:', error);
        alert('Failed to create lab');
      }
    }

  };

  return (
    <div className="ProfessorLabInfo-wrapper">
      <div className="ProfessorLabInfo-container">
        <h1 className="ProfessorLabInfo-h1">{
          user.labId ? `Edit Lab` : `Create a New Lab`
        }</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="name">Lab Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={labData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="labPosition">Lab Position</label>
            <input
              type="text"
              id="labPosition"
              name="labPosition"
              value={labData.labPosition}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="major">Major</label>
            <select
              id="major"
              name="major"
              value={labData.major}
              onChange={handleChange}
              required
            >
              <option value="">Select Major</option>
              {Object.keys(majors).map((major) => (
                <option key={major} value={major}>{major}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="submajor">Major Subcategory</label>
            <select
              id="submajor"
              name="submajor"
              value={labData.submajor}
              onChange={handleChange}
              required
            >
              <option value="">Select Subcategory</option>
              {labData.major && majors[labData.major].map((submajor) => (
                <option key={submajor} value={submajor}>{submajor}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={labData.description}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="expectations">Expectations</label>
            <textarea
              id="expectations"
              name="expectations"
              value={labData.expectations}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="language">Language</label>
            <select
              id="language"
              name="language"
              value={labData.language}
              onChange={handleChange}
              required
            >
              <option value="" disabled>Select a Language</option>
              {languages.map((language, index) => (
                <option key={index} value={language}>
                  {language}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group range" style={{ "--step": "10000", "--min": "0", "--max": "1000000" }}>
            <label className="ProfessorLabInfo-label" htmlFor="salary">Salary: {labData.salary.toLocaleString()} KRW</label>
            <input
              type="range"
              id="salary"
              name="salary"
              min="10000"
              max="1000000"
              step="10000"
              value={labData.salary}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group range" style={{ "--step": "1", "--min": "3", "--max": "10" }}>
            <label className="ProfessorLabInfo-label" htmlFor="maxSlots">Slots Available: {labData.maxSlots}</label>
            <input
              type="range"
              id="maxSlots"
              name="maxSlots"
              min="3"
              max="10"
              step="1"
              value={labData.maxSlots}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label className="ProfessorLabInfo-label" htmlFor="website">Website</label>
            <input
              type="text"
              id="website"
              name="website"
              value={labData.website}
              onChange={handleChange}
              required
            />
          </div>
          <div className="checkbox-group">
            <label className="ProfessorLabInfo-label" htmlFor="masters">Masters Continuation Required</label>
            <input
              type="checkbox"
              id="masters"
              name="masters"
              checked={labData.masters}
              onChange={handleChange}
            />
          </div>
          <button type="submit" className="ProfessorLabInfo-button">{
            user.labId ? `Edit Lab` : `Create Lab`
          }</button>
        </form>
      </div>
    </div>
  );
};

export default ProfessorLabInfo;
