import './styling/MailPopUp.css';
import {formatDate} from '../utils/FormatDate';
import axios from 'axios';
import React, {useState, useEffect} from 'react';

const MailPopUp = ({studentName, mail, mailType, isVisible, closeFunction}) => {

  const MAIL_URL = '/api/mail';

  const formattedDate = formatDate(mail.createdAt);

  let sendRec;
  if (mailType === 'incoming') {
    sendRec = `Sent by ${studentName} (${mail.senderId})`;
  } else {
    sendRec = `Sent to ${studentName} (${mail.recId})`;
  }

  return (
    <div className = {`MailOverlay ${isVisible ? 'visible' : 'hidden'}`} id = "mailOverlay">
        <div className = "PopUp" id = "popup">
          <button className = "PopUpExitButton"
                  id = "popUpExitButton"
                  onClick = {() => closeFunction()}>
            X
          </button>
          <div className = "MailWrapper">
            <div className = 'MailDetails'>
              <div className = 'MailDetailsHeader'>
                <label className = 'MailDetailsSendRec'>
                  {sendRec}
                </label>
                <label className = 'MailDetailsDate'>
                  {`Sent on ${formattedDate}`}
                </label>
                <label className = 'MailDetailsTitle'>
                  {`Title: ${mail.title}`}
                </label>
              </div>
              <p className = 'MailDetailsContent'>
                {mail.content}
              </p>
            </div>
          </div>
        </div>
      </div>
  );
};

export default MailPopUp;
