import React from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import { connect } from 'react-redux';
import { currentLinkAction } from '../actions';
import './styling/header.css';
import bell from '../icons/bell.png';
import briefcase from '../icons/briefcase.png';
import document from '../icons/document.png';
import home from '../icons/home.png';
import mail from '../icons/mail.png';
import monitor from '../icons/monitor.png';
import search from '../icons/search.png';

// Importing components
import PortfolioConfiguration from './Portfolio-Configuration';
import SearchLabs from './LabSearch';
import StudentReportSubmission from './StudentReportSubmission';
import Mail from './Mail';
import StudentAppList from './StudentApplicationList';
import StudentProfile from './StudentProfile';
import StudentDashboard from './StudentDashboard';

class StudentHeader extends React.Component {
  componentDidMount() {
    const path = window.location.pathname;
    const route = path.split('/').pop();
    console.log('Current route:', route);
    const routeActionMap = {
      'student-profile': this.updatePLinkState,
      'student-mail': this.updateMLinkState,
      'student-searchLabs': this.updateSLLinkState,
      'student-appList': this.updateALLinkState,
      'student-portfolioConfig': this.updatePCLinkState,
      'student-dashboard': this.updateDLinkState,
      'student-reportSubmission': this.updateRSLinkState
    };

    if (routeActionMap[route]) {
      console.log('Updating link state for route:', route);
      routeActionMap[route]();
    } else {
      console.log('No matching route found for:', route);
    }
  }

  updatePLinkState = () => {
    console.log('Updating link state to student-profile');
    this.props.currentLinkAction('student-profile');
  }

  updateMLinkState = () => {
    console.log('Updating link state to student-mail');
    this.props.currentLinkAction('student-mail');
  }

  updateSLLinkState = () => {
    console.log('Updating link state to student-searchLabs');
    this.props.currentLinkAction('student-searchLabs');
  }

  updateALLinkState = () => {
    console.log('Updating link state to student-appList');
    this.props.currentLinkAction('student-appList');
  }

  updatePCLinkState = () => {
    console.log('Updating link state to student-portfolioConfig');
    this.props.currentLinkAction('student-portfolioConfig');
  }

  updateDLinkState = () => {
    console.log('Updating link state to student-dashboard');
    this.props.currentLinkAction('student-dashboard');
  }

  updateRSLinkState = () => {
    console.log('Updating link state to student-reportSubmission');
    this.props.currentLinkAction('student-reportSubmission');
  }

  getTitle = () => {
    const { currentLinkState } = this.props;
    const titles = {
      'student-profile': 'Profile',
      'student-mail': 'Mail',
      'student-searchLabs': 'Search Labs',
      'student-appList': 'Application List',
      'student-portfolioConfig': 'Portfolio Configuration',
      'student-dashboard': 'Dashboard',
      'student-reportSubmission': 'Report Submission',
    };
    return titles[currentLinkState] || 'Dashboard';
  }

  render() {
    console.log('Rendering StudentHeader with current link state:', this.props.currentLinkState);
    return (
      <div className="header-wrapper">
        <div className="topbar">
          <div className="left-topbar">
            <Link to="/student-profile" id="student-profile" onClick={this.updatePLinkState} className="profilePictureIcon">
              pp
            </Link>
            <div>
              <img src={bell} className="icon-st notificationIcon icon-ltb" alt="Notifications" />
            </div>
            <Link to="/student-mail" id="student-mail" onClick={this.updateMLinkState}>
              <img className="icon-st mailIcon icon-ltb" src={mail} alt="Mail" />
            </Link>
          </div>
          <div className="right-topbar">
            <p className="rtb-title">
              {this.getTitle()}
            </p>
          </div>
        </div>

        <div className="bottomContent">
          <div className="sidebar">
            <div className="labSearchingList">
              <div className="header-l">
                Lab Searching
              </div>
              <Link to="/student-searchLabs" id="student-searchLabs" onClick={this.updateSLLinkState} className="subtopic">
                <img className="icon-st" src={search} alt="Search" />
                <p className="title-st">
                  SEARCH LABS
                </p>
              </Link>
              <Link to="/student-appList" id="student-appList" onClick={this.updateALLinkState} className="subtopic">
                <img className="icon-st" src={monitor} alt="Applications" />
                <p className="title-st">
                  APPLICATION <br></br> LIST
                </p>
              </Link>
              <Link to="/student-portfolioConfig" id="student-portfolioConfig" onClick={this.updatePCLinkState} className="subtopic">
                <img className="icon-st" src={briefcase} alt="Portfolio" />
                <p className="title-st">
                  PORTFOLIO CONFIG
                </p>
              </Link>
            </div>
            <div className="labManagementList">
              <div className="header-l">
                Lab Management
              </div>
              <Link to="/student-dashboard" id="student-dashboard" onClick={this.updateDLinkState} className="subtopic">
                <img className="icon-st" src={home} alt="Dashboard" />
                <p className="title-st">
                  DASHBOARD
                </p>
              </Link>
              <Link to="/student-reportSubmission" id="student-reportSubmission" onClick={this.updateRSLinkState} className="subtopic">
                <img className="icon-st" src={document} alt="Reports" />
                <p className="title-st">
                  REPORT SUBMISSION
                </p>
              </Link>
            </div>
          </div>
          <div className="main">
            <Routes>
              <Route path="/student-portfolioConfig" element={<PortfolioConfiguration />} />
              <Route path="/student-searchLabs" element={<SearchLabs />} />
              <Route path="/student-reportSubmission" element={<StudentReportSubmission />} />
              <Route path="/student-mail" element={<Mail />} />
              <Route path="/student-appList" element={<StudentAppList />} />
              <Route path="/student-profile" element={<StudentProfile />} />
              <Route path="/student-dashboard" element={<StudentDashboard />} />
            </Routes>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { currentLinkState: state.currentLinkState };
}

export default connect(mapStateToProps, { currentLinkAction })(StudentHeader);
