import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styling/UserDashboard.css';

const UserDashboard = () => {
  const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }

  const USER_URL = '/api/users';

  const [user, setUser] = useState({});
  const [funds, setFunds] = useState(null); // Add state to hold funds if user is a school

  useEffect(() => {
    const controller = new AbortController();

    const fetchUser = async () => {
      try {
        const response = await axios.get(`${USER_URL}/me`, {
          signal: controller.signal,
          headers: headers,
        });
        setUser(response.data.user);

        if (response.data.user.type === 'school') {
          // Fetch the available funds if user is a school
          const fundsResponse = await axios.get(`${USER_URL}/funds`, {
            signal: controller.signal,
            headers: headers,
          });
          setFunds(fundsResponse.data.funds);
        }
      } catch (error) {
        if (axios.isCancel(error)) {
          console.error(`Fetch reports request cancelled`);
        }
        else {
          console.error(`Error getting user:`, error);
        }
      }
    };

    fetchUser();

    return () => {
      controller.abort();
    };
  }, []);

  const renderContent = () => {
    if (user.type === 'donator') {
      return (
        <div className="UserDashboard-content">
          <h2>Donate using Tokens from Maschain</h2>
          <button>Donate Now</button>
        </div>
      );
    } else if (user.type === 'school') {
      return (
        <div className="UserDashboard-content">
          <h2>Check Available Funds</h2>
          <p>Available Funds: {funds !== null ? funds : 'Loading...'}</p>
        </div>
      );
    } else {
      return null;
    }
  };

  return (
    <div className="UserDashboard-wrapper">
      <div className="UserDashboard-header">
        <h1>{user.name}</h1>
      </div>
      <div className="UserDashboard-panel">
        {renderContent()}
      </div>
    </div>
  );
};

export default UserDashboard;
