import './styling/MailEntry.css';
import {formatDate} from '../utils/FormatDate'

const MailEntry = ({mail, mailType, onClickFunction}) => {
  const formattedDate = formatDate(mail.createdAt);

  let displayedUser;
  let readStatus = false;                       
  if (mailType === 'incoming'){
    displayedUser = `${mail.senderId}`;
    readStatus = mail.isRead;
  }
  else{
    displayedUser = `${mail.recId}`;
  }

  return (
    <div className = {`MailEntry-${readStatus}`} onClick = {onClickFunction}>
      <label className = "MailDate">
        {formattedDate}
      </label>
      <label className = "MailTitle">
        {mail.title}
      </label>
      <label className = "MailSendRec">
        {displayedUser}
      </label>
    </div>
  );
}

export default MailEntry;
