import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './styling/LabDetailsModal.css';
import user from '../icons/person.png';

const LabDetailsModal = ({ lab, prof, onClose, onApply}) => {
  const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const APP_URL = '/api/applications';
  const [apps, setApps] = useState([]);
  const [step, setStep] = useState(1);

  useEffect(() => {
    const controller = new AbortController();

    const fetchApps = async () => {
      try {
        const response = await axios.get(`${APP_URL}`, {
            signal: controller.signal,
            headers: headers,
        });
        setApps(response.data.applications);
      } catch (error) {
          if (axios.isCancel(error)) {
              console.error(`Fetch application count request cancelled`);
          }
          else {
              console.error(`Error getting application count:` , error);
          }
      }
    }
    fetchApps();

    return () => {
      controller.abort();
    };
  }, []);

  const isAppExists = (status) => {
    if (apps.filter(app => app.status === status).length > 0) {
      return true;
    }
    return false;
  }

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>X</button>
        { step === 1 && (<>
          <div className="modal-header">
            <div className="lab-info">
              <img 
                className="lab-image"
                src={prof.profile_pic || user} 
                alt='profile pic of professor'></img>
              <div className="lab-contact">
                <h2 className="lab-name">Prof. {prof.name}</h2>
                <p className="lab-website"><strong>Website:</strong> {lab.website}</p>
                <p className="lab-email"><strong>Email:</strong> {prof.email}</p>
              </div>
            </div>
            <div className="lab-position-title">
              <p><strong>Lab Position</strong></p>
              <h2>{lab.labPosition}</h2>
            </div>
          </div>
          <div className="professor-info">
            <div>
              <p><strong>Major</strong></p>
              <h2>{lab.major}</h2>
            </div>
            <div>
              <p><strong>Specialization</strong></p>
              <h2>{lab.submajor}</h2>
            </div>
            <div>
              <p><strong>Masters Continuation</strong></p>
              <h2>{lab.masters ? 'Mandatory' : 'Optional'}</h2>
            </div>
            <div>
              <p><strong>Language</strong></p>
              <h2>{lab.language}</h2>
            </div>
            <div>
              <p><strong>Salary</strong></p>
              <h2>{lab.salary}</h2>
            </div>
          </div>
          <div className="lab-details-section">
            <h4>Details</h4>
            <p>{lab.description}</p>
          </div>
          <div className="lab-expectations">
            <h4>Expectations</h4>
            <p>{lab.expectations}</p>
          </div>
          {
            isAppExists('Ongoing') ? (
              <h3>You currently have an ongoing application</h3>
            ) : isAppExists('Accepted') ? (
              <h3>You already are in a lab</h3>
            ) : <button 
                  className="apply-button"
                  onClick = {() => setStep(2)}>Apply</button>
          }</>)
        }
        {
          step === 2 && (
            <div className = 'confirmation'>
              <h1>Are you sure?</h1>
              <p>
                The portfolio you have made in the 'Portfolio Configuration' tab cannot be changed and will be sent to the professor immediately. 
              </p>
              <div>
                <button
                  onClick = {() => 
                    setStep(1)
                  }>No, Go Back</button>
                <button
                  onClick = {onApply}>Yes, Apply</button>
              </div>

            </div>
          )
        }
        
      </div>
    </div>
  );
};

export default LabDetailsModal;
