import React, {useState} from 'react';
import axios from 'axios';

import './styling/UserReportSubmission.css';

const UserReportSubmission = () => {
  const token = localStorage.getItem('token');
  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const UPLOADS_URL = '/api/uploads'
  const REPORT_URL = '/api/reports';

  const fixedUserID = "2022313287";
  const fixedProfID = "2000313287";
  const initialState = {
    prof: fixedProfID,
    title: '',
    text: '',
    file: null,
  };

  const [report, setReport] = useState(initialState);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleChange = (e) => {
    const {name, value} = e.target;
    setReport(prevState => ({
      ...prevState, [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);

    const fileSpan = document.getElementById('progressReportFileSubmissionSpan');
    if (e.target.files.length === 0) {
      fileSpan.textContent = "\u2022 No file chosen"
    }
    else {
      fileSpan.textContent = "\u2022 " + e.target.files[0].name;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const fileInput = document.getElementById('progressReportFileSubmissionInput');
    let fileId = null;

    if (fileInput.files.length > 0){
      //send file to database
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post(`${UPLOADS_URL}/file`, formData, {
          headers: headers,
        });
        console.log('File uploaded:', response.data);
        fileId = response.data.fileId;
        setSelectedFile(null);
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('Failed to upload file: ' + error.message);
      }

      console.log(report);

      const fileSpan = document.getElementById('progressReportFileSubmissionSpan');
      fileSpan.textContent = "\u2022 No file chosen";
    }

    const updatedReport = {...report, file: fileId };

    //send report to database
    try {
      const response = await axios.post(REPORT_URL, (fileId ? updatedReport : report), {
        headers: headers,
      });
      console.log('Report submitted:', response.data);
      //alert('Report submitted successfully!');
      document.getElementById('overlaySR').style.display = 'block';
      setReport(initialState);
    } catch (error) {
      console.error('Error submitting report:', error);
      alert('Failed to submit report: ' + error.message);
    }


    //document.getElementById('overlaySR').style.display = 'block';
  };

  function exitPopUp(){
    document.getElementById('overlaySR').style.display = 'none';
  }

  // function createNewEntry(){
  //   var newDiv = document.createElement("div");
  //   var newLabel = document.createElement("label");
  //   var newButton = document.createElement("button");

  //   newLabel.textContent = "code1.ipynb";
  //   newLabel.classList.add("ProgressReportFileSubmissionEntryName");

  //   newButton.textContent = "X";
  //   newButton.classList.add("ProgressReportFileSubmissionEntryX");
  //   newButton.addEventListener("click", function() {
  //     newDiv.parentNode.removeChild(newDiv);
  //   });

  //   newDiv.classList.add("ProgressReportFileSubmissionEntry");
  //   newDiv.appendChild(newButton);
  //   newDiv.appendChild(newLabel);

  //   document.getElementById('progressReportFileSubmissionFiles').appendChild(newDiv);
  // }

  return (
    <div className = "UserReportSubmission-wrapper">
      <div className = "Container">
        <h1 className = "PageTitle">
          Progress Report Submission
        </h1>
        <div className = "ProgressReportTitle">
          <h3 className = "ProgressReportTitleHeader">
            Progress Report Title
          </h3>
          <form className = "ProgressReportTitleForm">
            <input 
              className = "ProgressReportTitleFormInput" 
              type = "text" 
              id="progressReportTitle" 
              name="title"
              value = {report.title}
              onChange = {handleChange}>
            </input>
          </form>
        </div>
        <div className = "ProgressReportDetails">
          <h3 className = "ProgressReportDetailsHeader">
            Progress Report Details
          </h3>
          <form className = "ProgressReportDetailsForm">
            <textarea className = "ProgressReportDetailsFormTextArea"
                      id = "progressReportDetails" 
                      name = "text"
                      value = {report.text}
                      rows = "12"
                      onChange = {handleChange}>
            </textarea>
          </form>

        </div>
        <div className = "ProgressReportSubmission">
          <div className = "ProgressReportSubmissionLeft">
            <h3 className = "ProgressReportFileSubmissionTitle">
              File Submission
            </h3>
            <div className = "ProgressReportFileSubmission">
              <div className = "ProgressReportFileSubmissionTop">
                <img className = "ProgressReportFileSubmissionIconImage" 
                      src = "https://www.svgrepo.com/show/326644/folder-open-outline.svg"/>
                <form 
                  id = "progressReportFileSubmissionForm"
                  encType='multipart/form-data'>
                  <input 
                    type="file" 
                    className = "ProgressReportFileSubmissionInput"
                    id = "progressReportFileSubmissionInput"
                    onChange={handleFileChange}/>
                  <button 
                    className='ProgressReportFileSubmissionButton'
                    id = "progressReportFileSubmissionButton"
                    onClick={(e) => {
                      e.preventDefault();
                      document.getElementById('progressReportFileSubmissionInput').click();
                    }}>
                      Choose File
                  </button>
                </form>
              </div>
              <div className = "ProgressReportFileSubmissionBottom">
                <span
                  className='ProgressReportFileSubmissionSpan'
                  id = "progressReportFileSubmissionSpan">
                  {"\u2022 No file chosen"}
                </span>
              </div>
              {/*<div className = "ProgressReportFileSubmissionIcon">
              </div>
              <div className = "ProgressReportFileSubmissionFiles"
                    id = "progressReportFileSubmissionFiles">
                 <div className = "ProgressReportFileSubmissionEntry">
                  <label className = "ProgressReportFileSubmissionEntryName">
                    code1.ipynb has been uploaded
                  </label>
                  <button className = "ProgressReportFileSubmissionEntryX">X</button>
                </div>
              </div> */}
            </div>
          </div>
          <div className = "ProgressReportSubmissionRight">
            <button className = "ProgressReportSubmissionRightButton"
                    id = "submitButton"
                    onClick={handleSubmit}>
              Submit Report
            </button>
          </div>
        </div>
      </div>

      <div className = "OverlaySR" id = "overlaySR">
        <div className = "PopUpSR" id = "popupSR">
          <button className = "PopUpExitButtonSR"
                  id = "popUpExitButtonSR"
                  onClick={exitPopUp}
                  >
            X
          </button>
          <h1 className = "PopUpTextSR">
            Progress Report Submitted
          </h1>
        </div>
      </div>

    </div>
  )
}

export default UserReportSubmission;
