            <input 
                  type="text" 
                  name="name" 
                  value={portfolio.name} 
                  onChange={handleChange} 
                  placeholder="Full Name" 
                />
          </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Student ID</label>
            <input 
                  type="text" 
                  name="studentId" 
                  value={portfolio.studentId} 
                  onChange={handleChange} 
                  placeholder="Student ID" 
                  disabled
                />
          </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Current School Year</label>
            <select                   name="currentSchoolYear" 
                  value={portfolio.currentSchoolYear} 
                  onChange={handleChange}>
              <option>--Current School Year--</option>
              <option>Year 1 Semester 1</option>
              <option>Year 1 Semester 2</option>
              <option>Year 2 Semester 1</option>
              <option>Year 2 Semester 2</option>
              <option>Year 3 Semester 1</option>
              <option>Year 3 Semester 2</option>
              <option>Year 4 Semester 1</option>
              <option>Year 4 Semester 2</option>
            </select>
            <p>If on holiday, select the next school semester</p>
          </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Languages Spoken</label>
            <input 
                  type="text" 
                  name="languagesSpoken" 
                  value={portfolio.languagesSpoken} 
                  onChange={handleChange} 
                  placeholder="Languages" 
                />
          </div>
        </div>

        <div className="Portfolio-Configuration-section">
          <h2>Contact Information</h2>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Email</label>
            <input 
                  type="email" 
                  name="email" 
                  value={portfolio.email} 
                  onChange={handleChange} 
                  placeholder="Email" 
                />
            </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Phone Number</label>
            <input 
                  type="tel" 
                  name="phoneNumber" 
                  value={portfolio.phoneNumber} 
                  onChange={handleChange} 
                  placeholder="Phone Number" 
                />
          </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>KakaoID (optional)</label>
            <input 
                  type="text" 
                  name="kakaoId" 
                  value={portfolio.kakaoId} 
                  onChange={handleChange} 
                  placeholder="KakaoID" 
                />
          </div>
        </div>

        <div className="Portfolio-Configuration-section">
          <h2>Education & Skills</h2>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Major</label>
            <input 
                  type="text" 
                  name="major" 
                  value={portfolio.major} 
                  onChange={handleChange} 
                  placeholder="Major" 
                />
            </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Specialize / Interest</label>
            <input 
                  type="text" 
                  name="specializeInterest" 
                  value={portfolio.specializeInterest} 
                  onChange={handleChange} 
                  placeholder="Specialize/Interest" 
                />
          </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Average GPA</label>
            <input 
                  type="number" 
                  name="averageGpa" 
                  value={portfolio.averageGpa} 
                  onChange={handleChange} 
                  placeholder="GPA" 
                />
            </div>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Skills & Expertise</label>
            <textarea 
                  className='Portfolio-Configuration-textarea' 
                  name="skillsExpertise" 
                  value={portfolio.skillsExpertise} 
                  onChange={handleChange} 
                  placeholder='Skills & Expertise'
                ></textarea>
          </div>
        </div>

        <div className="Portfolio-Configuration-section">
          <h2 className='Portfolio-Configuration-h2'>Achievements & Experience</h2>
          <div className="Portfolio-Configuration-form-group">
            <label className='Portfolio-Configuration-label'>Achievements (optional)</label>
            <input 
                  type="text" 
                  name="achievements" 
                  value={portfolio.achievements} 
                  onChange={handleChange} 
                  placeholder="Achievements" 
                />
          </div>
          <div className="Portfolio-Configuration-form-group">
            <div className="Portfolio-Configuration-form-group">
              <label className='Portfolio-Configuration-label'>Experiences (optional)</label>
              <input 
                      type="text" 
                      name="experiences" 
                      value={portfolio.experiences} 
                      onChange={handleChange} 
                      placeholder="Experiences" 
                    />
            </div>
          </div>
        </div>

        <div className="Portfolio-Configuration-section">
          <h2 className='Portfolio-Configuration-h2'>Personal Statement</h2>
          <div className="Portfolio-Configuration-form-group">
          <textarea 
                  className='Portfolio-Configuration-textarea' 
                  name="personalStatement" 
                  value={portfolio.personalStatement} 
                  onChange={handleChange} 
                  placeholder="Personal Statement"
                ></textarea>
          </div>
        </div>

        <button className="Portfolio-Configuration-save-btn" type="submit"  onClick={handleSubmit}>Save Portfolio Changes</button>

      </div>
    </div>
    </div>
    </>
  )
}
export default PortfolioConfiguration;
