import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './styling/UserDashboard.css';
import './MailEntry'
import MailEntry from './MailEntry';
import ReportEntry from './ReportEntry';
import ReportPopUp from './ReportPopUp';
import MailPopUp from './MailPopUp';

const UserDashboard = () => {
  const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const MAIL_URL = '/api/mail';
  const REPORT_URL = '/api/reports';
  const NOTE_URL = '/api/notes';
  const USER_URL = '/api/users';

  const [user, setUser] = useState({});
  const [note, setNote] = useState('');
  const [mail, setMail] = useState([]);
  const [reports, setReports] = useState([]);
  const [curReport, setCurReport] = useState({});
  const [curMail, setCurMail] = useState({});
  const [isReportPopUpVisible, setReportPopUpVisible] = useState(false);
  const [isMailPopUpVisible, setMailPopUpVisible] = useState(false);

  useEffect(() => {
    const controller = new AbortController();

    const fetchUser = async () => {
        try {
            const response = await axios.get(`${USER_URL}/me`, {
                signal: controller.signal,
                headers: headers,
            });
            setUser(response.data.user);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch reports request cancelled`);
            }
            else {
                console.error(`Error getting mail:` , error);
            }
        }
    }; 

    const fetchMail = async () => {
        try {
            const response = await axios.get(`${MAIL_URL}/incoming`, {
                signal: controller.signal,
                headers: headers,
            });
            setMail(response.data.mail);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch mail request cancelled`);
            }
            else {
                console.error(`Error getting mail:` , error);
            }
        }
    }; 

    const fetchReports = async () => {
        try {
            const response = await axios.get(`${REPORT_URL}`, {
                signal: controller.signal,
                headers: headers,
            });
            setReports(response.data.reports);
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch reports request cancelled`);
            }
            else {
                console.error(`Error getting reports:` , error);
            }
        }
    };

    const fetchNote = async () => {
        try {
            const response = await axios.get(`${NOTE_URL}`, {
                signal: controller.signal,
                headers: headers,
            });
            setNote(response.data.note || '');
        } catch (error) {
            if (axios.isCancel(error)) {
                console.error(`Fetch note request cancelled`);
            }
            else {
                console.error(`Error getting note:` , error);
            }
        }
    }

    fetchUser();
    fetchMail();
    fetchReports();
    fetchNote();

    return () => {
        controller.abort();
    };
  }, []);


  const handleChange = (e) => {
      setNote(e.target.value);
  };

  const handleSave = async (e) => {
      e.preventDefault();
      try {
          const response = await axios.put(NOTE_URL, {note}, {
              headers: headers,
          });
          //console.log('Note saved:', response.data);
          alert('Note saved');
      } catch (error) {
          //console.error('Error saving:', error);
          alert('Failed to save note: ' + error.message);
      }
  };

  const viewMail = (mail) => {
    setCurMail(mail);
    setMailPopUpVisible(true);
  }

  const closeMail = () => {
    setMailPopUpVisible(false);

    const readMail = async () => {
      try {
        const response = await axios.put(`${MAIL_URL}/${curMail._id}`, {isRead: true}, {
          headers: headers,
        });
        console.log(response.data);
      } catch (error) {
        console.log('Failed to update read status of mail: ' + error);
      }
    };

    if(!curMail.isRead) {
      readMail();
      setCurMail(prevState => ({
        ...prevState, isRead: true,
      }));
    }
  }

  const viewReport = (report) => {
      //console.log(report.file ? true : false);
      setCurReport(report);
      setReportPopUpVisible(true);
  };

  const closeReport = () => {
      setReportPopUpVisible(false);
  };

return (
  <div className = "UserDashboard-wrapper">
<body>
  <div class="UserDashboard-header">
      <h1>Welcome To Lab Name</h1>
      <h2>{user.name}</h2>
  </div>
  <div class="UserDashboard-panel">
      <div class="UserDashboard-container single">
          <div class="UserDashboard-box incoming">
              <h3>Unread Mail</h3>
              <div class="UserDashboard-button-list">
                  {mail.length > 0 ? (
                      mail
                      .filter(currentMail => !currentMail.isRead)
                      .map((currentMail, index) => (
                      <MailEntry 
                          key = {index}
                          mail = {currentMail} 
                          mailType = 'incoming'
                          onClickFunction = {() => viewMail(currentMail)}/>
                          ))
                      ) : (
                          <p>No incoming mail.</p>
                  )}
              </div>
          </div>
      </div>
      <div class="UserDashboard-container">
          <div class="UserDashboard-box half">
              <h3>Submitted Progress Reports</h3>
              <div class="UserDashboard-button-list">
                  {reports.length > 0 ? (
                      reports.map((report, index) => (
                      <ReportEntry
                          className = "reportEntries"
                          key = {index}
                          report = {report} 
                          onClickFunction = {() => viewReport(report)}/>
                          ))
                      ) : (
                          <p>No reports.</p>
                  )}
              </div>
          </div>
          <div class="UserDashboard-box half">
              <h3>Personal Notes</h3>
              <form id="UserDashboard-noteForm">
                  <textarea 
                      id="noteText"
                      name='text'
                      value = {note.text} 
                      rows="4" 
                      cols="50" 
                      placeholder="Enter your notes here..."
                      onChange = {handleChange}>
                  </textarea>
                  <button 
                      onClick = {handleSave}>Save Note</button>
              </form>
          </div>
      </div>
  </div>
  <ReportPopUp 
      id = 'reportPopUp'
      UserName = ''
      report = {curReport}
      isVisible = {isReportPopUpVisible} 
      closeFunction = {() => closeReport()}/>
  <MailPopUp
      id = 'mailPopUp'
      UserName = ''
      mail = {curMail}
      mailType = 'incoming'
      isVisible = {isMailPopUpVisible}
      closeFunction = {() => closeMail()}/>
</body>
    </div>
  )
}

export default UserDashboard;
