import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './styling/professorAppList.css';
import PortfolioPopUp from './PortfolioPopUp';

const ProfessorAppList = () => {
  const token = localStorage.getItem('token');

  const headers = {};
  if (token) {
      headers['Authorization'] = `Bearer ${token}`;
  }

  const USER_URL = '/api/users';
  const APP_URL = '/api/applications';
  const PORTFOLIO_URL = '/api/portfolios';

  const [applications, setApplications] = useState([]);
  const [curPortfolio, setCurPortfolio] = useState({});
  const [isPortfolioPopUpVisible, setPortfolioPopUpVisible] = useState(false);
  const [editingId, setEditingId] = useState(null); // Track which application is being edited

  useEffect(() => {
    const controller = new AbortController();
    const fetchApps = async (req, res) =>  {
      try {
        const response = await axios.get(`${APP_URL}/plus`, {
          signal: controller.signal,
          headers: headers,
        })
        setApplications(response.data.applications.reverse());
      } catch (error) {
        console.error('Error fetching applications:', error)
      }
    }

    fetchApps();

    return () => {
      controller.abort();
    };
  }, []);

  const handleStatusChange = async (studentId, newStatus) => {
    // Send a request to update the server
    console.log(studentId);
    console.log(newStatus);

    try {
      const response = await axios.put(`${APP_URL}/${studentId}`, {status: newStatus}, {
        headers: headers,
      });
      console.log(response.data);
      setApplications(applications.map(app => app.studentId === studentId ? 
        {...app, status: newStatus} : app));
      setEditingId(null);
    } catch (error) {
      console.log('Failed to update status:', error)
    }
  };

  const viewPortfolio = (studentId) => {
    //console.log(report.file ? true : false);
    const fetchPortfolio = async (req, res) => {
      try {
        const response = await axios.get(`${PORTFOLIO_URL}/${studentId}`, {
          headers: headers,
        })
        console.log(response.data);
        setCurPortfolio(response.data.portfolio);
      } catch (error) {
        console.log('Failed to fetch portfolio:', error)
      }
    };
    fetchPortfolio();
    setPortfolioPopUpVisible(true);
  };

  const closePortfolio = () => {
    setPortfolioPopUpVisible(false);
    console.log(isPortfolioPopUpVisible);
  };

  return (
    <>
      <div className="applicationProfessor-wrapper">
        <div className="applicationProfessor-panel">
          <table>
            <thead>
              <tr>
                <th>Students' Name</th>
                <th>Student ID</th>
                <th>Major</th>
                <th>Portfolio</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {applications.map(app => (
                <tr key={app._id}>
                  <td>{app.studentInfo.name}</td>
                  <td>{app.studentId}</td>
                  <td>{app.studentInfo.major}</td>
                  <td>
                    <span
                      className = 'application-portfolio-span'
                      onClick={() => viewPortfolio(app.studentId)}>
                      View Portfolio
                    </span>
                  </td>
                  <td>
                    {editingId === app.studentId ? (
                      <select
                        value={app.status}
                        onChange={(e) => handleStatusChange(app.studentId, e.target.value)}
                        onBlur={() => setEditingId(null)} // Close dropdown if the user clicks away
                      >
                        <option value="Ongoing">Ongoing</option>
                        <option value="Accepted">Accepted</option>
                        <option value="Rejected">Rejected</option>
                      </select>
                    ) : (
                      <button
                        className={`applicationProfessor-status applicationProfessor-${app.status.toLowerCase()}`}
                        onClick={() => setEditingId(app.studentId)}
                      >
                        {app.status}
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <PortfolioPopUp 
      id = 'portfolioPopUp'
      portfolio = {curPortfolio}
      isVisible = {isPortfolioPopUpVisible} 
      closeFunction = {() => closePortfolio()}/>
    </>
  );
}

export default ProfessorAppList;
